package day8;

public class ArithmetiOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=39;
		int b=20;
		
		System.out.println(a+b);
		System.out.println(b-a);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		
		//Increment & Decrement Operators 
		
		
		System.out.println(++a);//Pre increment //40
		System.out.println(a++);//Post Increment //40
		System.out.println(a); //41
		
		System.out.println(--b);//Pre Decrement //19
		System.out.println(b--);//Post Decrement //19
		System.out.println(b);//18
		
	}

}
