package day8;

public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=40;
		int b=20;
		
		System.out.println(a>b);//true
		System.out.println(a<b);//false
		System.out.println(a>=b);//a>b or a=b //true
		System.out.println(a<=b);//false
		System.out.println(a==b);//false
		System.out.println(a!=b);//true
		
	}

}
